import React, { useState } from 'react';
import { FaLinkedin, FaGithub, FaEnvelope } from 'react-icons/fa';
import '../styles/Hero.css';

const Hero = () => {
  const [copySuccess, setCopySuccess] = useState(false);
  
  const copyEmailToClipboard = (e) => {
    e.preventDefault();
    const email = "dazhar@purdue.edu";
    navigator.clipboard.writeText(email)
      .then(() => {
        setCopySuccess(true);
        // Reset the success message after 2 seconds
        setTimeout(() => {
          setCopySuccess(false);
        }, 2000);
      })
      .catch(err => {
        console.error('Failed to copy email: ', err);
      });
  };

  return (
    <div className="hero-container">
      <h1 className="hero-name">Danish Azhar</h1>
        <p className="hero-title">Hi, I'm Turning Data into Impact</p>
    
        <div className="hero-roles">
        <span className="highlight">Data Scientist • AI Consultant • Product Optimizer</span>
        </div>


      <div className="social-icons">
        <a href="https://www.linkedin.com/in/danish-azhar/" target="_blank" rel="noopener noreferrer" className="social-icon">
          <FaLinkedin />
        </a>
        <a href="https://github.com/danishazhar14" target="_blank" rel="noopener noreferrer" className="social-icon">
          <FaGithub />
        </a>
        <div className="email-container" style={{ position: 'relative', display: 'inline-block' }}>
          <a 
            href="#" 
            onClick={copyEmailToClipboard} 
            className="social-icon"
            title="Click to copy email"
          >
            <FaEnvelope />
          </a>
          {copySuccess && (
            <span 
              style={{
                position: 'absolute',
                bottom: '-40px',
                left: '50%',
                transform: 'translateX(-50%)',
                backgroundColor: '#4ade80',
                color: '#001220',
                padding: '5px 10px',
                borderRadius: '4px',
                fontSize: '0.875rem',
                whiteSpace: 'nowrap',
                zIndex: 10
              }}
            >
              Email copied!
            </span>
          )}
        </div>
      </div>
      <div className="scroll-down">
        <span className="scroll-icon">&#8595;</span>
      </div>
    </div>
  );
};

export default Hero;
