import React, { useEffect, useState } from 'react';
import '../styles/login.css';
import ProfilePic from '../styles/MyImage.jpeg';

const MainPage = () => {
    const [typedName, setTypedName] = useState('');
    const fullName = "  I'M     CHANGEZI";

    const phrases = [
        '        A STUDENT           ',
        '        A FRONTEND DEVELOPER               ',
        '        A BACKEND DEVELOPER            ',
        'A MOB APP DEVELOPER        ',
        '         AN OPENAI EXPERT               '
    ];

    const [typedPhrase, setTypedPhrase] = useState('');
    const [phraseIndex, setPhraseIndex] = useState(0);
    const [isDeleting, setIsDeleting] = useState(false);
    const [currentPhrase, setCurrentPhrase] = useState(phrases[0]);
    const [charIndex, setCharIndex] = useState(0);

    // Projects state
    const projects = [
        { title: "Project 1", description: "Short description of Project 1." },
        { title: "Project 2", description: "Short description of Project 2." },
        { title: "Project 3", description: "Short description of Project 3." }
    ];
    const [currentProjectIndex, setCurrentProjectIndex] = useState(0);

    // Typing effect for full name
    useEffect(() => {
        let index = 0;

        const typingInterval = setInterval(() => {
            if (index < fullName.length) {
                setTypedName((prev) => prev + fullName.charAt(index));
                index++;
            } else {
                clearInterval(typingInterval); // Clear interval when done
            }
        }, 100); // Typing speed for full name

        return () => clearInterval(typingInterval);
    }, []); // Runs on component mount

    // Typing effect for phrases
    useEffect(() => {
        const typingInterval = setInterval(() => {
            if (isDeleting) {
                // Remove character
                setTypedPhrase((prev) => prev.slice(0, -1));
                if (typedPhrase.length === 0) {
                    setIsDeleting(false); // Switch to typing after deleting
                    setPhraseIndex((prev) => (prev + 1) % phrases.length); // Move to next phrase
                }
            } else {
                // Add character
                if (charIndex < currentPhrase.length) {
                    setTypedPhrase((prev) => currentPhrase.slice(0, charIndex + 1));
                    setCharIndex((prev) => prev + 1); // Move to next character
                } else {
                    // Start deleting after completing the current phrase
                    setIsDeleting(true);
                    setTimeout(() => {
                        setCharIndex(0); // Reset charIndex for the next phrase
                    }, 2000); // Wait for 2 seconds before starting to delete
                }
            }
        }, isDeleting ? 50 : 100); // Typing speed based on deleting state

        return () => clearInterval(typingInterval);
    }, [isDeleting, currentPhrase, charIndex, typedPhrase]); // Include typedPhrase in dependencies

    // Manage currentPhrase and resetting logic
    useEffect(() => {
        // Only update if isDeleting has just changed to false
        if (!isDeleting) {
            setCurrentPhrase(phrases[phraseIndex]);
            setTypedPhrase(''); // Reset typedPhrase for the new phrase
            setCharIndex(0); // Reset character index for the new phrase
        }
    }, [phraseIndex]);

    // Reset current phrase and state when deleting starts
    useEffect(() => {
        if (isDeleting) {
            setCurrentPhrase(phrases[phraseIndex]); // Ensure the current phrase is set when deleting
        }
    }, [isDeleting]);

    // Cycle through projects
    useEffect(() => {
        const projectInterval = setInterval(() => {
            setCurrentProjectIndex((prev) => (prev + 1) % projects.length);
        }, 6000); // Change project every 6 seconds (3 seconds for display + 3 seconds for transition)

        return () => clearInterval(projectInterval);
    }, []); // Runs on component mount

    // Pause before changing the project
    useEffect(() => {
        const pauseBeforeNextProject = setTimeout(() => {
            setCurrentProjectIndex((prev) => (prev + 1) % projects.length);
        }, 3000); // Show each project for 3 seconds

        return () => clearTimeout(pauseBeforeNextProject);
    }, [currentProjectIndex]); // Depend on currentProjectIndex

    return (
        <>
            <div className="header-about-container">
                <header>
                    <h1>{typedName}</h1>
                    <nav>
                        <ul>
                            <li><a href="#about">About</a></li>
                            <li><a href="#projects">Projects</a></li>
                            <li><a href="#skills">Skills</a></li>
                            <li><a href="#experience">Experience</a></li>
                            <li><a href="#awards">Awards</a></li>
                            <li><a href="#contact">Contact</a></li>
                        </ul>
                    </nav>
                </header>
                
                <section id="about" className="about-section">
                    <div className="profile-pic-container">
                        <img src={ProfilePic} alt="My Profile" className="profile-pic" />
                    </div>
                    <br /><br /> <br /><br />
                    <div className="about-section">
                        <p style={{ textAlign: 'center', color: 'white', fontWeight: 'bold' }}>
                            <span style={{ fontWeight: 'bold', fontSize: '1.2em', color: 'white' }}>Hi,</span> My name is <span style={{ fontWeight: 'bold', fontSize: '1.2em', color: 'white' }}>Mehdi,</span> and I am {' '}
                            <span style={{ fontWeight: 'bold', color: '#00bccde5', fontSize: '1.8em' }}>{typedPhrase}</span>.
                        </p>
                    </div>
                    <div className="social-icons">
                        <a href="https://www.linkedin.com/in/your-linkedin-profile" target="_blank" rel="noopener noreferrer">
                            <i className="fab fa-linkedin" />
                        </a>
                        <a href="https://twitter.com/your-twitter-handle" target="_blank" rel="noopener noreferrer">
                            <i className="fab fa-twitter" />
                        </a>
                        <a href="https://wa.me/your-whatsapp-number" target="_blank" rel="noopener noreferrer">
                            <i className="fab fa-whatsapp" />
                        </a>
                        <a href="mailto:your-email@gmail.com">
                            <i className="fas fa-envelope" />
                        </a>
                    </div>
                </section>
            </div>

            <section id="projects" className="projects-section">
                <div className="container">
                    <h2 style={{ fontWeight: 'bold', color: '#00bccde5', fontSize: '2.5em' }}>PROJECTS</h2>
                    <div className="projects-grid">
                        <div className="project-card">
                            <h3>{projects[currentProjectIndex].title}</h3>
                            <p>{projects[currentProjectIndex].description}</p>
                        </div>
                    </div>
                </div>
            </section>

            <section id="skills" className="skills-section">
                <div className="container">
                    <h2>Skills</h2>
                    <ul>
                        <li>JavaScript</li>
                        <li>React</li>
                        <li>Node.js</li>
                        <li>MongoDB</li>
                        <li>Express</li>
                        <li>CSS</li>
                    </ul>
                </div>
            </section>

            <section id="experience" className="experience-section">
                <div className="container">
                    <h2>Experience</h2>
                    <div className="experience-grid">
                        <div className="experience-card">
                            <h3>Job Title 1</h3>
                            <p>Details about job experience, the company, and the skills used.</p>
                        </div>
                        <div className="experience-card">
                            <h3>Job Title 2</h3>
                            <p>Details about job experience, the company, and the skills used.</p>
                        </div>
                    </div>
                </div>
            </section>

            <section id="awards" className="awards-section">
                <div className="container">
                    <h2>Awards</h2>
                    <ul>
                        <li>Award 1 - Description of the award</li>
                        <li>Award 2 - Description of the award</li>
                    </ul>
                </div>
            </section>

            <section id="contact" className="contact-section">
                <div className="container">
                    <h2>Contact Me</h2>
                    <form action="#" method="POST">
                        <div className="form-group">
                            <label htmlFor="name">Name</label>
                            <input type="text" id="name" name="name" required />
                        </div>
                        <div className="form-group">
                            <label htmlFor="email">Email</label>
                            <input type="email" id="email" name="email" required />
                        </div>
                        <div className="form-group">
                            <label htmlFor="message">Message</label>
                            <textarea id="message" name="message" required></textarea>
                        </div>
                        <button type="submit">Send Message</button>
                    </form>
                </div>
            </section>

            <footer>
                <div className="container">
                    <p>&copy; 2024 My Portfolio. All rights reserved.</p>
                </div>
            </footer>
        </>
    );
};

export default MainPage;
