import React, { useEffect, useState } from 'react';
import '../styles/login.css';
import ProfilePic from '../styles/MyImage.jpeg';
// import csslogo from '<i class="fa-brands fa-html5"></i>';

import axios from 'axios'; // Ensure you install axios for making HTTP requests



const MainPage = () => {
    const [typedName, setTypedName] = useState('');
    const [hasScrolledToProjects, setHasScrolledToProjects] = useState(false); // Track if the user has scrolled to projects section
    const fullName = "  I'M     CHANGEZI";

    const phrases = [
        '        A STUDENT           ',
        '        A FRONTEND DEVELOPER               ',
        '        A BACKEND DEVELOPER            ',
        'A MOB APP DEVELOPER        ',
        '         AN OPENAI EXPERT               '
    ];

    const [typedPhrase, setTypedPhrase] = useState('');
    const [phraseIndex, setPhraseIndex] = useState(0);
    const [isDeleting, setIsDeleting] = useState(false);
    const [currentPhrase, setCurrentPhrase] = useState(phrases[0]);
    const [charIndex, setCharIndex] = useState(0);



    
const downloadCV = async () => {
    try {
        const response = await axios.post('/api/download-cv', {}, {
            responseType: 'blob', // Ensures the response is treated as a file (binary data)
        });

        // Create a link element, set the URL using the response, and trigger the download
        const url = window.URL.createObjectURL(new Blob([response.data]));
        const link = document.createElement('a');
        link.href = url;
        link.setAttribute('download', 'Mehdi_Resume.pdf'); // Specify the filename for download
        document.body.appendChild(link);
        link.click();
        link.remove();
    } catch (error) {
        console.error('Error downloading the CV:', error);
    }
};

    // Typing effect for full name
    useEffect(() => {
        let index = 0;
        const typingInterval = setInterval(() => {
            if (index < fullName.length) {
                setTypedName((prev) => prev + fullName.charAt(index));
                index++;
            } else {
                clearInterval(typingInterval);
            }
        }, 100); 

        return () => clearInterval(typingInterval);
    }, []); 

    // Typing effect for phrases
    useEffect(() => {
        const typingInterval = setInterval(() => {
            if (isDeleting) {
                setTypedPhrase((prev) => prev.slice(0, -1));
                if (typedPhrase.length === 0) {
                    setIsDeleting(false);
                    setPhraseIndex((prev) => (prev + 1) % phrases.length);
                }
            } else {
                if (charIndex < currentPhrase.length) {
                    setTypedPhrase((prev) => currentPhrase.slice(0, charIndex + 1));
                    setCharIndex((prev) => prev + 1);
                } else {
                    setIsDeleting(true);
                    setTimeout(() => {
                        setCharIndex(0);
                    }, 20000);
                }
            }
        }, isDeleting ? 50 : 100);

        return () => clearInterval(typingInterval);
    }, [isDeleting, currentPhrase, charIndex, typedPhrase]);

    // Manage currentPhrase and resetting logic
    useEffect(() => {
        if (!isDeleting) {
            setCurrentPhrase(phrases[phraseIndex]);
            setTypedPhrase('');
            setCharIndex(0);
        }
    }, [phraseIndex]);

    // Detect scrolling to the projects section
    useEffect(() => {
        const handleScroll = () => {
            const projectsSection = document.getElementById('projects');
            const sectionPosition = projectsSection.getBoundingClientRect().top;
            const screenHeight = window.innerHeight;

            // Check if the section is in view and the animation hasn't run before
            if (sectionPosition < screenHeight && !hasScrolledToProjects) {
                setHasScrolledToProjects(true); // Trigger the animation
            }
        };

        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, [hasScrolledToProjects]);

    return (
        <>
            <div className="header-about-container">
                <header>
                    <h1>{typedName}</h1>
                    <nav>
                        <ul>
                            <li><a href="#about">About</a></li>
                            <li><a href="#projects">Projects</a></li>
                            <li><a href="#skills">Skills</a></li>
                            <li><a href="#experience">Experience</a></li>
                            <li><a href="#awards">Awards</a></li>
                            <li><a href="#contact">Contact</a></li>
                        </ul>
                    </nav>
                </header>

                <section id="about" className="about-section">
                    <div className="profile-pic-container">
                        <img src={ProfilePic} alt="My Profile" className="profile-pic" />
                    </div>
                    <br /><br />
                    <div className="about-section">
                        <p style={{ textAlign: 'center', color: 'white', fontWeight: 'bold' }}>
                            <span style={{ fontWeight: 'bold', fontSize: '1.2em', color: 'white' }}>Hi,</span> My name is <span style={{ fontWeight: 'bold', fontSize: '1.2em', color: 'white' }}>Mehdi,</span> and I am {' '}
                            <span style={{ fontWeight: 'bold', color: '#00bccde5', fontSize: '1.8em' }}>{typedPhrase}</span>.
                        </p>
                    </div>
                    <div className="social-icons">
                        <a href="https://www.linkedin.com/in/your-linkedin-profile" target="_blank" rel="noopener noreferrer">
                            <i className="fab fa-linkedin" />
                        </a>
                        <a href="https://twitter.com/your-twitter-handle" target="_blank" rel="noopener noreferrer">
                            <i className="fab fa-twitter" />
                        </a>
                        <a href="https://wa.me/your-whatsapp-number" target="_blank" rel="noopener noreferrer">
                            <i className="fab fa-whatsapp" />
                        </a>
                        <a href="mailto:your-email@gmail.com">
                            <i className="fas fa-envelope" />
                        </a>
                    </div>

                    <div style={{ textAlign: 'center', marginTop: '20px' }}>
                    <a href="path_to_your_cv/YourCV.pdf" download="YourCV.pdf" class="btn-download-cv">Download RESUME</a>
                    </div>
                </section>
            </div>


            <section id="projects" className="projects-section">
                    <div className={`container ${hasScrolledToProjects ? 'slide-in' : ''}`}>
                        <h2 style={{ fontWeight: 'bold', color: '#00bccde5', fontSize: '2.5em', textAlign: 'center' }}>PROJECTS</h2>
                        <div className="projects-grid">
                            <div className="project-card">
                                <h3>Project 1</h3>
                                <p>Short description of the project goes here. Explain what technology you used and the features implemented.</p>
                                <a href="https://github.com/your-repo-1" target="_blank" rel="noopener noreferrer" className="project-link">
                                    View Project
                                </a>
                            </div>
                            <div className="project-card">
                                <h3>Project 2</h3>
                                <p>Short description of the project goes here. Explain what technology you used and the features implemented.</p>
                                <a href="https://github.com/your-repo-2" target="_blank" rel="noopener noreferrer" className="project-link">
                                    View Project
                                </a>
                            </div>
                            <div className="project-card">
                                <h3>Project 3</h3>
                                <p>Short description of the project goes here. Explain what technology you used and the features implemented.</p>
                                <a href="https://github.com/your-repo-3" target="_blank" rel="noopener noreferrer" className="project-link">
                                    View Project
                                </a>
                            </div>
                            {/* Add more projects similarly */}
                        </div>
                    </div>
                </section>
           





            <section id="skills" className="skills-section">
    <div className="container">
    <h2 style={{ fontWeight: 'bold', color: '#00bccde5', fontSize: '2.5em', textAlign: 'center' }}>Skills</h2>
        <div className="skills-grid">
            <div className="skill-item">
                <a href="https://www.geeksforgeeks.org/javascript-tutorial/" target="_blank" rel="noopener noreferrer">
                    <i className="fab fa-js-square skill-logo"></i> {/* JavaScript */}
                </a>
            </div>
            <div className="skill-item">
                <a href="https://www.geeksforgeeks.org/reactjs-tutorials/" target="_blank" rel="noopener noreferrer">
                    <i className="fab fa-react skill-logo"></i> {/* React */}
                </a>
            </div>
            <div className="skill-item">
                <a href="https://www.geeksforgeeks.org/nodejs-tutorials/" target="_blank" rel="noopener noreferrer">
                    <i className="fab fa-node skill-logo"></i> {/* Node.js */}
                </a>
            </div>
            <div className="skill-item">
                <a href="https://www.geeksforgeeks.org/mongodb-tutorial/" target="_blank" rel="noopener noreferrer">
                    <i className="fas fa-database skill-logo"></i> {/* MongoDB */}
                </a>
            </div>
            <div className="skill-item">
                <a href="https://www.geeksforgeeks.org/express-js/" target="_blank" rel="noopener noreferrer">
                    <i className="fab fa-node-js skill-logo"></i> {/* Express */}
                </a>
            </div>
            <div className="skill-item">
                <a href="https://www.geeksforgeeks.org/css/" target="_blank" rel="noopener noreferrer">
                    <i className="fab fa-css3-alt skill-logo"></i> {/* CSS */}
                </a>
            </div>
            <div className="skill-item">
                <a href="https://aws.amazon.com/training/" target="_blank" rel="noopener noreferrer">
                    <i className="fab fa-aws skill-logo"></i> {/* AWS Cloud */}
                </a>
            </div>
            <div className="skill-item">
                <a href="https://learn.unity.com/" target="_blank" rel="noopener noreferrer">
                    <i className="fab fa-unity skill-logo"></i> {/* Unity */}
                </a>
            </div>
           
        </div>
    </div>
</section>


            



            <section id="experience" className="experience-section">
                <div className="container">
                <h2 style={{ fontWeight: 'bold', color: '#00bccde5', fontSize: '2.5em', textAlign: 'center' }}>Experience</h2>
                    <div className="experience-grid">
                        <div className="experience-card">
                            <h3>Job Title 1</h3>
                            <p>Details about job experience, the company, and the skills used.</p>
                        </div>
                        <div className="experience-card">
                            <h3>Job Title 2</h3>
                            <p>Details about job experience, the company, and the skills used.</p>
                        </div>
                    </div>
                </div>
            </section>

            <section id="awards" className="awards-section">
                <div className="container">
                <h2 style={{ fontWeight: 'bold', color: '#00bccde5', fontSize: '2.5em', textAlign: 'center' }}>Awards</h2>
                    <ul>
                        <li>Award 1 - Description of the award</li>
                        <li>Award 2 - Description of the award</li>
                    </ul>
                </div>
            </section>

            <section id="contact" className="contact-section">
                <div className="container">
                <h2 style={{ fontWeight: 'bold', color: '#00bccde5', fontSize: '2.5em', textAlign: 'center' }}>Contact</h2>
                    <form action="#" method="POST">
                        <div className="form-group">
                            <label htmlFor="name">Name</label>
                            <input type="text" id="name" name="name" required />
                        </div>
                        <div className="form-group">
                            <label htmlFor="email">Email</label>
                            <input type="email" id="email" name="email" required />
                        </div>
                        <div className="form-group">
                            <label htmlFor="message">Message</label>
                            <textarea id="message" name="message" required></textarea>
                        </div>
                        <button type="submit">Send Message</button>
                    </form>
                </div>
            </section>

        </>
    );
};

export default MainPage;
