const AnimatedSkillsTrack = ({ skills, iconPrefix = "skill" }) => {
  // Duplicate the skills array to create a seamless loop effect
  const duplicatedSkills = [...skills, ...skills];
  
  return (
    <div className="skills-track-container">
      <div className="skills-track">
        {duplicatedSkills.map((skill, index) => (
          <div key={`${iconPrefix}-${index}`} className="animated-skill-item">
            {/* You can add icons here if you have them */}
            <span>{skill}</span>
          </div>
        ))}
      </div>
    </div>
  );
};

const SkillsSection = () => {
  // Define your skills by category
  const dataSkills = ["Python", "R", "TensorFlow", "XGBoost", "Scikit-learn", "Random Forest", "Pandas", "NumPy", "PyTorch"];
  const dataEngineeringSkills = ["SQL", "Apache Spark", "Snowflake", "Databricks", "SAS-EM", "ETL", "Data Pipelines"];
  const visualizationSkills = ["Tableau", "Looker Studio", "Excel", "Google Analytics", "Power BI", "Data Visualization"];
  const cloudTools = ["AWS ", "Azure ", "Figm ", "PuLP", "Solver", "Docker", "Kubernetes"];

  return (
    <section id="skills" className="section">
      <div className="section-container">
        <h2 className="section-title">Tech Stack</h2>
        <div className="section-content">
          
          <div className="skills-category">
            <h3 className="skills-category-header">Data Science & ML</h3>
            <AnimatedSkillsTrack skills={dataSkills} iconPrefix="data" />
          </div>
          
          <div className="skills-category">
            <h3 className="skills-category-header">Data Engineering</h3>
            <AnimatedSkillsTrack skills={dataEngineeringSkills} iconPrefix="engineering" />
          </div>
          
          <div className="skills-category">
            <h3 className="skills-category-header">Visualization & Analytics</h3>
            <AnimatedSkillsTrack skills={visualizationSkills} iconPrefix="viz" />
          </div>
          
          <div className="skills-category">
            <h3 className="skills-category-header">Cloud & Tools</h3>
            <AnimatedSkillsTrack skills={cloudTools} iconPrefix="cloud" />
          </div>
          
        </div>
      </div>
    </section>
  );
};

export default SkillsSection;
